function hideHeader(){document.querySelector("header").classList.add("header--is-hidden")}function showHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function closeOpenAnswer(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function clearButton(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function hideTabs(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?hideHeader:showHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);hideTabs(),t.classList.add("shows__list--is-active"),clearButton(),e.target.classList.add("shows__tabs__button--is-active")});for(let e=0;e<a.length;e++)a[e].addEventListener("click",closeOpenAnswer)});